mapper.insert:
insert into notify_box (id, is_read, user_id, 
  notify_type, notify_id, create_time
  )
values (#{id,jdbcType=BIGINT}, #{isRead,jdbcType=BIT}, #{userId,jdbcType=BIGINT}, 
  #{notifyType,jdbcType=INTEGER}, #{notifyId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select *
from notify_box
where user_id = #{userId}
order by create_time;
